mapper.select:
SELECT groupId , configId , value , configName , configType , configDesc , c.firmId , f.firmName , c.createBy , c.createDate , c.updateBy , c.updateDate FROM bw_config c LEFT JOIN bw_firm f ON c.firmid = f.firmid
c.configId = #{configId}
AND c.firmId LIKE #{firmId}
AND groupId = #{groupId}

mapper.update:
UPDATE bw_config

    value = #{value}

        , configName = #{configName}


        , configType = #{configType}


        , configDesc = #{configDesc}

    , updateBy = #{updateBy}
    , updateDate = current_timestamp


    configId = #{configId}

        AND firmId LIKE #{firmId}


        AND groupId = #{groupId}

mapper.delete:
DELETE
FROM bw_config
WHERE configId = #{configId}
  AND firmId = #{firmId}

mapper.select:
SELECT f.firmId ,f.firmName ,f.firmLoc ,f.firmRegion ,f.grade ,f.smallIcon ,f.largeIcon ,f.addr ,f.postcode ,f.contact ,f.phone ,f.phone2 ,f.fax ,f.email ,f.memo ,f.preinit , COUNT(m.meterId) meterCount FROM bw_firm f LEFT JOIN bw_meter m ON f.firmId = m.firmId
f.firmId LIKE #{firmId}
GROUP BY f.firmId

mapper.insert:
INSERT INTO bw_firm(firmId, firmName

,grade
,smallIcon
,largeIcon
,addr
,postcode
,contact
,phone
,phone2
,fax
,email
,preinit)
VALUES(#{firmId}, #{firmName}

, #{grade}
, #{smallIcon}
, #{largeIcon}
, #{addr}
, #{postcode}
, #{contact}
, #{phone}
, #{phone2}
, #{fax}
, #{email}
, false)

mapper.update:
UPDATE bw_firm


        firmName = #{firmName}


        , firmLoc = st_geomFromText(#{firmLoc})::point


        , firmRegion = st_geomFromText(#{firmRegion})::polygon


        ,grade = #{grade}


        ,smallIcon = #{smallIcon}


        ,largeIcon = #{largeIcon}


        ,addr = #{addr}


        ,postcode = #{postcode}


        ,contact = #{contact}


        ,phone = #{phone}


        ,phone2 = #{phone2}


        ,fax = #{fax}


        ,email = #{email}


        ,memo = #{memo}


WHERE firmId = #{firmId}
