mapper.select:
SELECT d.dmaId , d.dmaName , ST_AsText(d.dmaLoc::geometry) AS dmaLoc , ST_AsText(d.dmaRegion::geometry) AS dmaRegion , d.showType , dl.statDate AS lastLossDate , dl.parentPhyloss1 AS lastLoss , dl.numSeconds , dl.phylossRate AS lastLossRate , dl.comments AS comments FROM bw_dma d LEFT JOIN bw_dma_loss dl ON d.dmaId = dl.dmaId LEFT JOIN ( SELECT dmaId , MAX(statDate) AS statDate FROM bw_dma_loss
AND dmaId IN
#{did}
AND dmaId LIKE #{dmaId}
GROUP BY dmaId ) dlm ON d.dmaId = dlm.dmaId AND (dlm.statDate = dl.statDate OR dlm.statDate - interval '1 day' = dl.statDate)

SELECT d.dmaId
,d.dmaName
,ST_AsText(d.dmaLoc::geometry) AS dmaLoc
,ST_AsText(d.dmaRegion::geometry) AS dmaRegion
,d.showType
,dl.statDate AS lastLossDate
,dl.parentPhyloss1 AS lastLoss
,dl.numSeconds AS numSeconds
,dl.phylossRate AS lastLossRate
,dl.flowMax
,dl.flowMin
,dl.avgFlow
,dl.comments AS comments
FROM bw_dma d
LEFT JOIN
(
SELECT * FROM bw_dma_loss


        firmId LIKE #{firmId}



            AND dmaId IN

                #{did}



            AND dmaId LIKE #{dmaId}




            AND statDate = #{statDate}


            AND statDate BETWEEN #{statDate1} AND #{statDate2}


            AND statDate >= #{statDate1}


            AND statDate <= #{statDate2}



) AS dl ON d.dmaId = dl.dmaId


        d.firmId LIKE #{firmId}



            AND d.dmaId IN

                #{did}



            AND d.dmaId LIKE #{dmaId}



ORDER BY dl.statDate DESC
LIMIT 0, 2000

SELECT d.dmaId ,d.dmaName ,ST_AsText(d.dmaLoc::geometry) AS dmaLoc ,ST_AsText(d.dmaRegion::geometry) AS dmaRegion ,d.showType ,dl.statDate AS lastLossDate ,dl.parentPhyloss1 AS lastLoss ,dl.numSeconds ,dl.phylossRate AS lastLossRate ,dl.flowMax ,dl.flowMin ,dl.avgFlow ,dl.comments FROM bw_dma d LEFT JOIN ( SELECT dmaId , MAX(statDate) AS statDate FROM bw_dma_loss
firmId LIKE #{firmId}
AND dmaId IN
#{did}
AND dmaId LIKE #{dmaId}
AND statDate = #{statDate}
AND statDate BETWEEN #{statDate1} AND #{statDate2}
AND statDate >= #{statDate1}
AND statDate <= #{statDate2}
GROUP BY dmaId ) AS dlm ON d.dmaId = dlm.dmaId LEFT JOIN bw_dma_loss dl ON dlm.dmaId = dl.dmaId AND (dlm.statDate = dl.statDate OR dlm.statDate - INTERVAL '1 DAY' = dl.statDate)
d.firmId LIKE #{firmId}
AND d.dmaId IN
#{did}
AND d.dmaId LIKE #{dmaId}
ORDER BY dl.statDate DESC LIMIT 0, 2000
