mapper.select:
SELECT zoneId , zoneName , zoneType , dmaId , level , leafType , memo , bigMeterCount , resiMeterCount , onlineBigMeterCount , onlineResiMeterCount , ST_AsText(zoneLoc) AS zoneLoc , ST_AsText(zoneRegion) AS zoneRegion , firmId , leafable , createBy , createDate , updateBy , updateDate FROM bw_zone AS z
to_tsvector('parser_name', z.zoneName) @@ to_tsquery('parser_name', #{keywords})
fetchPyzm(tsvector_to_array(to_tsvector('parser_name', z.zoneName))) @> tsvector_to_array(to_tsvector('parser_name', #{pinyin}))
AND zoneId LIKE #{zoneId}
AND zoneId IN
#{zid}
AND zoneName LIKE #{zoneName}
AND firmId LIKE #{subFirmId}

mapper.insert:
INSERT INTO bw_zone(
                    zoneid
