                    , zonetype
                    , level
                    , leaftype
                    , memo
                    , bigmetercount
                    , resimetercount
                    , onlinebigmetercount
                    , onlineresimetercount

                    , firmid
                    , leafable
                    , createby
                    , createdate
                    , updateby
                    , updatedate
) VALUES (
           #{zoneId}
         , #{zoneName}
         , #{zoneType}
         , #{level}
         , #{leafType}
         , #{memo}
         , #{bigMeterCount}
         , #{resiMeterCount}
         , #{onlineBigMeterCount}
         , #{onlineResiMeterCount}

         , #{firmId}
         , #{leafable}
         , #{createBy}
         , Now()
         , #{updateBy}
         , Now()
)

mapper.delete:
DELETE FROM bw_zone AS z

    zoneId = #{zoneId}


        AND zoneName LIKE #{zoneName}


    AND firmId LIKE #{subFirmId}

mapper.update:
UPDATE bw_zone

    , zoneName = #{zoneName}
    , zoneType = #{zoneType}
    , level = #{level}
    , leafType = #{leafType}
    , memo = #{memo}
    , bigMeterCount = #{bigMeterCount}
    , resiMeterCount = #{resiMeterCount}
    , onlineBigMeterCount = #{onlineBigMeterCount}
    , onlineResiMeterCount = #{onlineResiMeterCount}

        , ST_AsText = ST_GeomFromText(#{zoneLoc})


        , ST_AsText = ST_GeomFromText(#{zoneRegion})

    , leafable = #{leafable}
    , updateBy = #{updateBy}
    , updateDate = NOW()


    zoneId = #{zoneId}
    AND firmId LIKE #{firmId}

mapper.select:
SELECT zoneId , meterId , flowOut , childType FROM bw_zone_meter
zoneId = #{zoneId}
AND firmId LIKE #{firmId}

mapper.insert:
INSERT INTO bw_zone_meter(zoneid, meterid, flowout, childtype)
SELECT zm.meterId
    , zm.zoneId
    , zm.flowOut
    , zm.childType
    FROM (
        VALUES

    (
    #{meter.meterId}
    , #{zoneId}
    , #{meter.flowOut}
    , #{meter.childType}
    )

) AS zm(meterId
, zoneId
, flowOut
, childType)
JOIN bw_meter m ON zm.meterId = m.meterId

mapper.delete:
DELETE FROM bw_zone_meter AS bzm

    AND zoneId = #{zoneId}

        AND meterId IN (

            #{meter.meterId}

        )
